define("ckeditor/_source/plugins/a11yhelp/lang/fr",["dijit","dojo","dojox"],function(){CKEDITOR.plugins.setLang("a11yhelp","fr",{accessibilityHelp:{title:"Instructions pour l'accessibilit\u00e9",contents:"Contenu de l'aide. Pour fermer ce dialogue, appuyez sur la touche ESC (Echappement).",legend:[{name:"G\u00e9n\u00e9ral",items:[{name:"Barre d'outils de l'\u00e9diteur",legend:"Appuyer sur ${toolbarFocus} pour acc\u00e9der \u00e0 la barre d'outils. Se d\u00e9placer vers les groupes suivant ou pr\u00e9c\u00e9dent de la barre d'outil avec les touches TAB et SHIFT-TAB. Se d\u00e9placer vers les boutons suivant ou pr\u00e9c\u00e9dent de la barre d'outils avec les touches FLECHE DROITE et FLECHE GAUCHE. Appuyer sur la barre d'espace ou la touche ENTRER pour activer le bouton de barre d'outils."},
{name:"Dialogue de l\u00e9diteur",legend:"A l'int\u00e9rieur d'un dialogue, appuyer sur la touche TAB pour naviguer jusqu'au champ de dalogue suivant, appuyez sur les touches SHIFT + TAB pour revenir au champ pr\u00e9c\u00e9dent, appuyez sur la touche ENTRER pour soumettre le dialogue, appuyer sur la touche ESC pour annuler le dialogue. Pour les dialogues avec plusieurs pages d'onglets, appuyer sur ALT + F10 pour naviguer jusqu'\u00e0 la liste des onglets. Puis se d\u00e9placer vers l'onglet suivant avec la touche TAB ou FLECHE DROITE. Se d\u00e9placer vers l'onglet pr\u00e9c\u00e9dent avec les touches SHIFT + TAB ou FLECHE GAUCHE. Appuyer sur la barre d'espace ou la touche ENTRER pour s\u00e9lectionner la page de l'onglet."},
{name:"Menu contextuel de l'\u00e9diteur",legend:"Appuyer sur ${contextMenu} ou entrer le RACCOURCI CLAVIER pour ouvrir le menu contextuel. Puis se d\u00e9placer vers l'option suivante du menu avec les touches TAB ou FLECHE BAS. Se d\u00e9placer vers l'option pr\u00e9c\u00e9dente avec les touches  SHIFT+TAB ou FLECHE HAUT. appuyer sur la BARRE D'ESPACE ou la touche ENTREE pour s\u00e9lectionner l'option du menu. Oovrir le sous-menu de l'option courante avec la BARRE D'ESPACE ou les touches ENTREE ou FLECHE DROITE. Revenir \u00e0 l'\u00e9l\u00e9ment de menu parent avec les touches ESC ou FLECHE GAUCHE. Fermer le menu contextuel avec ESC."},
{name:"Zone de liste en menu d\u00e9roulant de l'\u00e9diteur",legend:"A l'int\u00e9rieur d'une liste en menu d\u00e9roulant, se d\u00e9placer vers l'\u00e9l\u00e9ment suivant de la liste avec les touches TAB ou FLECHE BAS. Se d\u00e9placer vers l'\u00e9l\u00e9ment pr\u00e9c\u00e9dent de la liste avec les touches SHIFT + TAB ou FLECHE HAUT. Appuyer sur la BARRE D'ESPACE ou sur ENTREE pour s\u00e9lectionner l'option dans la liste. Appuyer sur ESC pour fermer le menu d\u00e9roulant."},{name:"Barre d'emplacement des \u00e9l\u00e9ments de l\u00e9diteur",
legend:"Appuyer sur ${elementsPathFocus} pour naviguer vers la barre d'emplacement des \u00e9l\u00e9ments de l\u00e9diteur. Se d\u00e9placer vers le bouton d'\u00e9l\u00e9ment suivant avec les touches TAB ou FLECHE DROITE. Se d\u00e9placer vers le bouton d'\u00e9l\u00e9ment pr\u00e9c\u00e9dent avec les touches SHIFT+TAB ou FLECHE GAUCHE. Appuyer sur la BARRE D'ESPACE ou sur ENTREE pour s\u00e9lectionner l'\u00e9l\u00e9ment dans l'\u00e9diteur."}]},{name:"Commandes",items:[{name:" Commande d\u00e9faire",
legend:"Appuyer sur ${undo}"},{name:" Commande refaire",legend:"Appuyer sur ${redo}"},{name:" Commande gras",legend:"Appuyer sur ${bold}"},{name:" Commande italique",legend:"Appuyer sur ${italic}"},{name:" Commande soulign\u00e9",legend:"Appuyer sur ${underline}"},{name:" Commande lien",legend:"Appuyer sur ${link}"},{name:" Commande enrouler la barre d'outils",legend:"Appuyer sur ${toolbarCollapse}"},{name:" Aide Accessibilit\u00e9",legend:"Appuyer sur ${a11yHelp}"}]}]}})});