define("ckeditor/plugins/a11yhelp/lang/fi",["dijit","dojo","dojox"],function(){CKEDITOR.plugins.setLang("a11yhelp","fi",{accessibilityHelp:{title:"Saavutettavuus ohjeet",contents:"Ohjeen sis\u00e4ll\u00f6t. Sulkeaksesi t\u00e4m\u00e4n dialogin paina ESC.",legend:[{name:"Yleinen",items:[{name:"Editorin ty\u00f6kalupalkki",legend:"Paina ${toolbarFocus} siirty\u00e4ksesi ty\u00f6kalupalkkiin. Siirry seuraavaan ja edelliseen ty\u00f6kalupalkin ryhm\u00e4\u00e4n TAB ja SHIFT-TAB n\u00e4pp\u00e4imill\u00e4. Siirry seuraavaan ja edelliseen ty\u00f6kalupainikkeeseen k\u00e4ytt\u00e4m\u00e4ll\u00e4 NUOLI OIKEALLE tai NUOLI VASEMMALLE n\u00e4pp\u00e4imill\u00e4. Paina V\u00c4LILY\u00d6NTI tai ENTER n\u00e4pp\u00e4int\u00e4 aktivoidaksesi ty\u00f6kalupainikkeen."},
{name:"Editorin dialogi",legend:"Dialogin sis\u00e4ll\u00e4, painamalla TAB siirryt seuraavaan dialogin kentt\u00e4\u00e4n, painamalla SHIFT+TAB siirryt aiempaan kentt\u00e4\u00e4n, painamalla ENTER l\u00e4het\u00e4t dialogin, painamalla ESC peruutat dialogin. Dialogeille joissa on useita v\u00e4lilehti\u00e4, paina ALT+F10 siirty\u00e4ksesi v\u00e4lillehtilistaan. Siirty\u00e4ksesi seuraavaan v\u00e4lilehteen paina TAB tai NUOLI OIKEALLE. Siirry edelliseen v\u00e4lilehteen painamalla SHIFT+TAB tai nuoli vasemmalle. Paina V\u00c4LILY\u00d6NTI tai ENTER valitaksesi v\u00e4lilehden."},
{name:"Editorin oheisvalikko",legend:"Paina ${contextMenu} tai SOVELLUSPAINIKETTA avataksesi oheisvalikon. Liiku seuraavaan valikon vaihtoehtoon TAB tai NUOLI ALAS n\u00e4pp\u00e4imill\u00e4. Siirry edelliseen vaihtoehtoon SHIFT+TAB tai NUOLI YL\u00d6S n\u00e4pp\u00e4imill\u00e4. Paina V\u00c4LILY\u00d6NTI tai ENTER valitaksesi valikon kohdan. Avataksesi nykyisen kohdan alivalikon paina V\u00c4LILY\u00d6NTI tai ENTER tai NUOLI OIKEALLE painiketta. Siirty\u00e4ksesi takaisin valikon ylemm\u00e4lle tasolle paina ESC tai NUOLI vasemmalle. Oheisvalikko suljetaan ESC painikkeella."},
{name:"Editorin listalaatikko",legend:"Listalaatikon sis\u00e4ll\u00e4 siirry seuraavaan listan kohtaan TAB tai NUOLI ALAS painikkeilla. Siirry edelliseen listan kohtaan SHIFT+TAB tai NUOLI YL\u00d6S painikkeilla. Paina V\u00c4LILY\u00d6NTI tai ENTER valitaksesi listan vaihtoehdon. Paina ESC sulkeaksesi listalaatikon."},{name:"Editorin elementtipolun palkki",legend:"Paina ${elementsPathFocus} siirty\u00e4ksesi elementtipolun palkkiin. Siirry seuraavaan elementtipainikkeeseen TAB tai NUOLI OIKEALLE painikkeilla. Siirry aiempaan painikkeeseen SHIFT+TAB tai NUOLI VASEMMALLE painikkeilla. Paina V\u00c4LILY\u00d6NTI tai ENTER valitaksesi elementin editorissa."}]},
{name:"Komennot",items:[{name:"Peruuta komento",legend:"Paina ${undo}"},{name:"Tee uudelleen komento",legend:"Paina ${redo}"},{name:"Lihavoi komento",legend:"Paina ${bold}"},{name:"Kursivoi komento",legend:"Paina ${italic}"},{name:"Alleviivaa komento",legend:"Paina ${underline}"},{name:"Linkki komento",legend:"Paina ${link}"},{name:"Pienenn\u00e4 ty\u00f6kalupalkki komento",legend:"Paina ${toolbarCollapse}"},{name:"Saavutettavuus ohjeet",legend:"Paina ${a11yHelp}"}]}]}})});