define("ckeditor/plugins/a11yhelp/lang/pt-br",["dijit","dojo","dojox"],function(){CKEDITOR.plugins.setLang("a11yhelp","pt-br",{accessibilityHelp:{title:"Instru\u00e7\u00f5es de Acessibilidade",contents:"Conte\u00fado da Ajuda. Para fechar este di\u00e1logo pressione ESC.",legend:[{name:"Geral",items:[{name:"Barra de Ferramentas do Editor",legend:"Pressione ${toolbarFocus} para navegar para a barra de ferramentas. Mova para o anterior ou pr\u00f3ximo grupo de ferramentas com TAB e SHIFT-TAB. Mova para o anterior ou pr\u00f3ximo bot\u00e3o com SETA PARA DIREITA or SETA PARA ESQUERDA. Pressione ESPA\u00c7O ou ENTER para ativar o bot\u00e3o da barra de ferramentas."},
{name:"Di\u00e1logo do Editor",legend:"Dentro de um di\u00e1logo, pressione TAB para navegar para o pr\u00f3ximo campo, pressione SHIFT + TAB para mover para o campo anterior, pressione ENTER para enviar o di\u00e1logo, pressione ESC para cancelar o di\u00e1logo. Para di\u00e1logos que tem m\u00faltiplas abas, pressione ALT + F10 para navegar para a lista de abas, ent\u00e3o mova para a pr\u00f3xima aba com SHIFT + TAB ou SETA PARA ESQUERDA. Pressione ESPA\u00c7O ou ENTER para selecionar a aba."},
{name:"Menu de Contexto do Editor",legend:"Pressione ${contextMenu} ou TECLA DE MENU para abrir o menu de contexto, ent\u00e3o mova para a pr\u00f3xima op\u00e7\u00e3o com TAB ou SETA PARA BAIXO. Mova para a anterior com SHIFT+TAB ou SETA PARA CIMA. Pressione ESPA\u00c7O ou ENTER para selecionar a op\u00e7\u00e3o do menu. Abra o submenu da op\u00e7\u00e3o atual com ESPA\u00c7O ou ENTER ou SETA PARA DIREITA. Volte para o menu pai com ESC ou SETA PARA ESQUERDA. Feche o menu de contexto com ESC."},{name:"Caixa de Lista do Editor",
legend:"Dentro de uma caixa de lista, mova para o pr\u00f3ximo item com TAB ou SETA PARA BAIXO. Mova para o item anterior com SHIFT + TAB ou SETA PARA CIMA. Pressione ESPA\u00c7O ou ENTER para selecionar uma op\u00e7\u00e3o na lista. Pressione ESC para fechar a caixa de lista."},{name:"Barra de Caminho do Elementos do Editor",legend:"Pressione ${elementsPathFocus} para a barra de caminho dos elementos. Mova para o pr\u00f3ximo bot\u00e3o de elemento com TAB ou SETA PARA DIREITA. Mova para o bot\u00e3o anterior com  SHIFT+TAB ou SETA PARA ESQUERDA. Pressione ESPA\u00c7O ou ENTER para selecionar o elemento no editor."}]},
{name:"Comandos",items:[{name:" Comando Desfazer",legend:"Pressione ${undo}"},{name:" Comando Refazer",legend:"Pressione ${redo}"},{name:" Comando Negrito",legend:"Pressione ${bold}"},{name:" Comando It\u00e1lico",legend:"Pressione ${italic}"},{name:" Comando Sublinhado",legend:"Pressione ${underline}"},{name:" Comando Link",legend:"Pressione ${link}"},{name:" Comando Fechar Barra de Ferramentas",legend:"Pressione ${toolbarCollapse}"},{name:" Ajuda de Acessibilidade",legend:"Pressione ${a11yHelp}"}]}]}})});